INSERT INTO "public"."travel_reviews" ("id", "created_at", "updated_at", "profile_id", "rating", "title", "description_text", "map_link", "image_path", "place_title") VALUES ('2b095022-d943-4bba-ad6e-0a432d060b17', '2025-11-25 10:05:32.695829+00', '2025-11-25 10:05:32.695829+00', '0c18026d-762a-401f-82c9-50f715a0626e', '4', 'Arashiyama Bamboo Forest', 'Beautiful and otherworldly, but extremely crowded, even early in the morning. It''s definitely worth seeing for the scale of the bamboo, but the experience felt rushed and less serene than expected due to the volume of tourists.', 'https://maps.app.goo.gl/HRdhRyqmPGGcqjZd9', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764065132653.jpg', null), ('4bab3737-e4b6-4ace-a320-62ee0e241011', '2025-11-25 16:06:36.546529+00', '2025-11-25 16:06:36.546529+00', '282d84c5-4388-4b28-b0b5-1cf27d51c2fe', '5', 'Chula uni', 'wonderful', 'https://maps.app.goo.gl/Vgj9tnjqMVikmHJRA', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764086794610.webp', null), ('51a1bb06-02f3-4565-8e3b-433648f5458a', '2025-11-25 09:47:11.642113+00', '2025-11-25 09:47:11.642113+00', 'f53c67f1-ff3a-4397-9663-b6da63246618', '5', 'The Peak Tram', 'A must-do for stunning panoramic views of Victoria Harbour and the Kowloon skyline. The ascent is incredibly steep and exciting, but expect long queues during peak hours. Go on a clear day for the best photos!', 'https://maps.app.goo.gl/EFPNksBqzw1VyesK7', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764064031929.jpg', null), ('5df51a9d-0815-4598-8f18-f827ba3dcd33', '2025-11-25 10:40:20.716793+00', '2025-11-25 10:40:20.716793+00', '6bd8ddbb-334e-4d00-a201-4d253563e9ea', '4', 'KMUTT', 'Good Uni, Good life', 'https://maps.app.goo.gl/ApByM3KoEn2KE4bj6', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764067221008.jpg', null), ('5e213586-4cc6-488d-b37e-25c29697743b', '2025-11-25 16:19:30.26907+00', '2025-11-25 16:19:30.26907+00', '282d84c5-4388-4b28-b0b5-1cf27d51c2fe', '4', 'Central Rama 2', 'Big', 'https://maps.app.goo.gl/vMLJaT7V7ydpVh8n7', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764087566331.png', null), ('b2df355a-e167-48cb-8baa-c832859d39f6', '2025-11-25 10:11:14.672788+00', '2025-11-25 10:11:14.672788+00', '0c18026d-762a-401f-82c9-50f715a0626e', '5', 'CMU', 'The main highlight is the Ang Kaew Reservoir, a serene lake within the grounds that''s perfect for jogging or enjoying the sunrise. The university maintains a vibrant, green, and sustainable environment. A top-tier institution with a wonderful, calm learning atmosphere.', 'https://maps.app.goo.gl/PUhFRT5sqX7Be1Qd7', 'https://xiakmjiawpnktgfkbkqn.supabase.co/storage/v1/object/public/review-images/review-1764065474900.jpg', null);
